import {Fragment ,useContext , useRef } from 'react'
import classes from './MealItemForm.module.css'
import CartContext from './CartContext'
const MealForm = props =>
{
    
    const {cart,addItem,setCart} = useContext(CartContext);
    const amountInput = useRef();

    const handleSubmit = (event) =>
    {
        event.preventDefault();
        if(amountInput.current.value !== "0")
        {
            let sameItem = false;
            let amount  = 0;
           
            cart.map((item) =>
            {
                if(item.data.name === props.data.name)
                {
                    sameItem = true;
                    return;
                }
            });
            if(sameItem)
            {
                let newCart = cart.map((item) =>{
                    if(item.data.name === props.data.name)
                    {
                        return {data : props.data ,amount : (parseInt(amountInput.current.value) + item.amount)}
                    }
                    else
                    {
                        return item;
                    }
                })
                setCart(newCart)
            }
            else{
            addItem({data : props.data,amount : amountInput.current.value})
            }
            amountInput.current.value = "";
        }
    }
    
    return(
        <Fragment>
            <div className ={classes.form}>
            <form onSubmit ={handleSubmit}>
                <label htmlFor ='amount'>{props.label}</label>
                <input id ='amount' ref ={amountInput} ></input>
               <button className ={classes.button} type = "submit">{props.btnLabel}</button>
            </form>
            </div>
        </Fragment>
    );
}

export default MealForm;
