import MealItem from "./MealItem";
import classes from "./Meals.module.css";
import {useEffect,useState} from 'react'


const DUMMY_MEALS = [
  {
    id: "m1",
    name: "Gyoza",
    description: "Man Tan Tan's famous gyoza.",
    price: 47,
  },
  {
    id: "m2",
    name: "Gasmino's kebab",
    description: "A kebab served in a pita.",
    price: 32,
  },
  {
    id: "m3",
    name: "Vitrina Burger",
    description: "With cheese and caramelized onions.",
    price: 55,
  },
  {
    id: "m4",
    name: "Shawarma Hkosem",
    description: "With tahini and amaba...",
    price: 51,
  },
];


const Meals = (props) => {

  const[meals,setMeals] = useState([])

  async function fetchMeals()
  {
    const response = await fetch("https://meal-app-react-default-rtdb.europe-west1.firebasedatabase.app/meals.json")
    const data = await response.json();
    console.log(data);
    mealsList = Object.values(data);
    setMeals(mealsList)
  }

  useEffect(()=>{
    fetchMeals();
  },[meals])
  
  return (
    <div>
      <div className={classes.meals}>
        <ul className={classes.ul}>
          {meals.map((meal,index) => {
            return <MealItem data={meal} key={index}></MealItem>;
          })}
        </ul>
      </div>
    </div>
  );
};

export default Meals;
